info_template.template:
{% macro formatBool(val) -%}
{% if val == 't' %}
    {{ 'Ja' }}
{% elif val == 'f' %}
    {{ 'Nein' }}
{% else %}
    {{ val }}
{% endif %}
{%- endmacro %}
<table class="attribute-list">
  <tr>
    <td class="identify-attr-title wrap"><i>Zone:</i></td>
    <td class="identify-attr-title wrap">{{ feature.typ}}</td>
  </tr>
  <tr>
    <td class="identify-attr-title wrap"><i>RRB-Datum:</i></td>
    <td class="identify-attr-title wrap">{{ feature.rechtskraftdatum }}</td>
  </tr>
  <tr>
    <td class="identify-attr-title wrap"><i>Gesetzeskonform:</i></td>
    <td class="identify-attr-title wrap">{{ formatBool(feature.gesetzeskonform) }}</td>
  </tr>
  <tr>
    <td class="identify-attr-title wrap" rowspan={{ feature.dokumente|length }}-1><i>Dokumente:</i></td>
      {% for dokument in feature.dokumente_txt %}
          <td class="identify-attr-value wrap" style="padding-left: 0;padding-right: 0.25em;"><a href={{ dokument.URL }} target="_blank">{{ dokument.Bezeichnung }}</a></td>
        </tr>
        <tr>
      {% endfor %}
    </tr>
</table>

